import React, { useState, useEffect } from "react";
import TinderCard from "react-tinder-card";
import "../components/TinderCards.css"
import database from "../firebase";

function TinderCards() {
  const [people, setPeople] = useState([
    {
      name: "Harry Alpha",
      url:'https://www.biography.com/.image/ar_1:1%2Cc_fill%2Ccs_srgb%2Cg_face%2Cq_auto:good%2Cw_300/MTY2MzU3OTcxMTUwODQxNTM1/steve-jobs--david-paul-morrisbloomberg-via-getty-images.jpg'
    },
    {
      name: "Bob Geldoff ",
      url:'https://www.randomlists.com/img/people/harry_houdini.jpg'
    },
  ]);


        useEffect(() => {
            database.collection('people').onSnapshot(snapshot => (
                setPeople(snapshot.docs.map(doc => doc.data()))
            ))
        }, [people])

  return (
    <div>

    <div class = "tinderCards_cardContainer">
    {people.map((person) => (
        <TinderCard 
            className="swipe" 
            key={person.name}
            preventSwipe={['up', 'down']}>
          <div
            style={{ backgroundImage: `url(${person.url})` }}
            className="card"
          >
            <h3>{person.name}</h3>
          </div>
        </TinderCard>
      ))}
    </div>

    </div>
  );
}

export default TinderCards;
