import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import './navbar.css'
import { Button } from './button';


function Navbar() {
    const [click, setClick] = useState(false);
    const[button, setButton] = useState(true)

    const handleClick = () => setClick(!click);
    const closeMenu = () => setClick(false);

    /*
    const showButton = () => {
        if(window.innerWidth <= 960) {
            setButton(false)
        }
        else{
            setButton(true)
        }
    };

    window.addEventListener('resize', showButton);
    */

    return (
        <>
            <nav className='navbar'>
                <div className='navbar-container'>
                    <Link to ='/' className="navbar-logo">
                        Windler 
                        <i className = 'fas fa-wind' />
                    </Link>
                    <div className= 'menu-icon' onClick ={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-bars' } />
                    </div>
                    <ul className={click ? 'nav-menu  active' : 'nav-menu'}>
                        <li className='nav-item'>
                            <Link to='/explore' className='nav-links' onClick={closeMenu}>
                            Explore
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/matches' className='nav-links' onClick={closeMenu}>
                            Matches
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/chat' className='nav-links' onClick={closeMenu}>
                            Chat
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/profile' className='nav-links' onClick={closeMenu}>
                            Profile
                            </Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/sign-up' className='nav-links-sign' onClick={closeMenu}>
                            Sign Up
                            </Link>
                        </li>
                    </ul>
                    {button && <Button buttonStyle = 'btn--outline'> Sign Up</Button>}
                </div>
            </nav>
        </>
    );
}

export default Navbar
